import React, { useState } from "react";
import emailjs from "emailjs-com";
import { useForm } from "react-hook-form";

export default function Contacts ()  {
    const [successMessage, setSuccessMessage] = useState("");
    const { register, handleSubmit, errors} = useForm();

    const serviceID = "service_ID";
    const templateID = "template_ID";
    const userID = "user_8FLqdeLJXQM67fje6JxaQ";

    const onSubmit = (data, r) => {
        sendEmail(
            serviceID, 
            templateID, 
            { 
                name: data.name,
                phone: data.phone,
                email: data.email,
                subject: data.subject,
                description: data.description
            }, 
            userID
        )
        r.target.reset();
    }

    const sendEmail = (serviceID, templateID, variables, userID) => {
        emailjs.send(serviceID, templateID, variables, userID )
          .then(() => {
            setSuccessMessage("Form sent successfully! I'll contact you as soon as possible.");
          }).catch(err => console.error(`Something went wrong ${err}`));
      }

    return (
        <div className="contacts">
            <div className="text-center">
            <h1>Contact Me</h1>
            <p>Please fill out the form below and describe your proyect needs and I'll constact you soon as posible.</p>
            <span className="success-message">{successMessage}</span>
            </div>
            <div className="container">
                <form onSubmit={handleSubmit(onSubmit)}>
                <div className="row">
                   <div className="col-md-6 col-xm-12">
                       {/* NAME INPUT */}
                       <div className="text-center">
                       <input 
                       className="form-control"
                       type="text"
                       placeholder="Name" 
                       name="name"
                       ref={
                        register({
                          required: "Please enter your name",
                          maxLength: {
                            value: 20,
                            message: "Please enter a name with fewer than 20 characters"
                          }
                        })
                      }
                       />
                       <div className="line"></div>
                       </div>
                       <span className="error-message">
                           {errors.name && errors.name.message}
                       </span>
                       {/* PHONE INPUT */}
                       <div className="text-center">
                       <input 
                       className="form-control"
                       type="text"
                       placeholder="Phone Number" 
                       name="phone"
                       ref={
                        register({
                          required: "Please add your phone number",
                        })
                      }
                       />
                       <div className="line"></div>
                       </div>
                       <span className="error-message">
                        {errors.phone && errors.phone.message}
                       </span>
                       {/* EMAIL INPUTS */}
                       <div className="text-center">
                       <input 
                       className="form-control"
                       type="text"
                       placeholder="Email" 
                       name="email"
                       ref={
                        register({
                          required: "Please provide you email",
                          pattern: {
                            value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                            message: "invalid Email"
                          }
                        })
                      }
                       />
                       <div className="line"></div>
                       </div>
                       <span className="error-message">
                       {errors.email && errors.email.message}
                       </span>
                       {/* SUBJECT INPUT */}
                       <div className="text-center">
                       <input 
                       className="form-control"
                       type="text"
                       placeholder="Subject" 
                       name="subject"
                       ref={
                        register({
                          required: "OOPS, you forget to add the subject.",
                        })
                      }
                       />
                       <div className="line"></div>
                       </div>
                       <span className="error-message">
                        {errors.subject && errors.subject.message}
                       </span>
                    </div>
                    <div className="col-md-6 col-xs-12">
                        {/* DESCRIPTION */}
                       <div className="text-center">
                       <textarea 
                       className="form-control"
                       type="text"
                       placeholder="Please describe shortly your project" 
                       name="description"
                       ref={
                        register({
                          required: "Please describe shortly your project needs...",
                        })
                      }
                       ></textarea>
                       <div className="line"></div>
                       </div>
                       <span className="error-message">
                        {errors.description && errors.description.message}
                       </span>
                       <button className="btn-main-offer contact-btn" type="submit">Contact Me</button>
                    </div>
                </div>
              </form>
           </div>
        </div>
    )
}
