import React, { useState } from "react";
import emailjs from "emailjs-com";
import { useForm } from "react-hook-form";

const Contacts = () => {
  const [successMessage, setSuccessMessage] = useState("");
  const { register, handleSubmit, formState:{ errors }} = useForm();

  const serviceID = "service_ID";
  const templateID = "template_ID";
  const userID = "user_8FLqdeLJXQM67fje6JxaQ";

  const onSubmit = (data, r) => { 

    sendEmail(
      serviceID,
      templateID,
      {
        name: data.name,
        phone: data.phone,
        email: data.email,
        subject: data.subject,
        description: data.description
      },
      userID
    )
    r.target.reset();
  }

  const sendEmail = (serviceID, templateID, variables, userID) => {
    emailjs.send(serviceID, templateID, variables, userID)
      .then(() => {
        setSuccessMessage("Form sent successfully! I'll contact you as soon as possible.");
      }).catch(err => console.error(`Something went wrong ${err}`));
  }

  return (
    <div className="contacts">
      <div className="text-center">
        <h1>contact me</h1>
        <p>Please fill out the form and describe you project needs and I'll contact you as soon as possible.</p>
        <span className="success-message">{successMessage}</span>
      </div>
      <div className="container">
        <form onSubmit={handleSubmit(onSubmit)}>
          <div className="row">
            <div className="col-md-6 col-xs-12">
              {/* NAME INPUT */}
              <div className="text-center">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Name"
                  name="name"
                  {...register
                    ("message",{
                    required: "Please enter your name",
                    maxLength: 
                        {
                        value: 20
                        }
                    })
                  }
                />
                <div className="line"></div>
              </div>

                {errors.name && <span className="error-message">Please enter a name with fewer than 20 characters</span>}

              {/* PHONE INPUT */}
              <div className="text-center">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Phone Number"
                  name="phone"
                  {...register({
                      required: true
                    })
                  }
                />
                <div className="line"></div>
              </div>

                {errors.phone && <span className="error-message">Invalid phone number</span>}

              {/* EMAIL INPUT */}
              <div className="text-center">
                <input
                  type="email"
                  className="form-control"
                  placeholder="Email"
                  name="email"
                  {...register({
                      required: true,
                      pattern: {
                        value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i
                      }
                    })
                  }
                />
                <div className="line"></div>
              </div>

                {errors.email && <span className="error-message">Invalid Email</span>}

              {/* SUBJECT INPUT */}
              <div className="text-center">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Subject"
                  name="subject"
                  {...register({
                      required: true
                    })
                  }
                />
                <div className="line"></div>
              </div>

                {errors.subject && <span className="error-message">OOPS, Don't you forget to add the subject.</span>}

            </div>
            <div className="col-md-6 col-xs-12">
              {/* DESCRIPTION */}
              <div className="text-center">
                <textarea
                  type="text"
                  className="form-control"
                  placeholder="Please describe shortly you project..."
                  name="description"
                  {...register({
                      required: true
                    })
                  }
                ></textarea>
                <div className="line"></div>
              </div>

                {errors.description && <span className="error-message"> Please describe shortly your project needs... </span>}

              <button className="btn-main-offer contact-btn" type="submit">contact me</button>
            </div>
          </div>
        </form>
      </div>
    </div>
  )
}

export default Contacts;





