import React, { useState } from "react";
import emailjs from "emailjs-com";
import { useForm } from "react-hook-form";

const Contacts = () => {
    const [ setSuccessMessage] = useState("");
    const { register, handleSubmit, errors} = useForm();

    const serviceID = "service_ID";
    const templateID = "template_ID";
    const userID = "user_8FLqdeLJXQM67fje6JxaQ";

    const onSubmit = (data, r) => {
        sendEmail(
            serviceID, 
            templateID, 
            { 
                name: data.name, 
                phone: data.phone,
                email: data.email,
                subject: data.subject,
                description: data.description
            }, 
            userID
        )
        r.target.reset();
    }

    function sendEmail(serviceID, templateID, variables, userID) {
        emailjs.sendForm(serviceID, templateID, variables, userID )
          .then(() => {
            setSuccessMessage("Form sent successfully! I'll contact you as soon as possible.");
          }).catch(err => console.error(`Something went wrong ${err}`));
      }
    return (
        <div className="contacts">
            <div className="text-center">
            <h1>Contact Me</h1>
            <p>Please fill out the form below and describe your proyect needs and I'll constact you soon as posible.</p>
            </div>
            <div className="container">
                <form onSubmit={handleSubmit(onSubmit)}>
                <div className="row">
                   <div className="col-md-6 col-xm-12">
                       {/* NAME INPUT */}
                       <div className="text-center">
                       <input 
                       className="form-control"
                       type="text"
                       placeholder="Name" 
                       name="name"
                       ref={
                        register({
                          required: "Please enter your name",
                          maxLength: {
                            value: 20,
                            message: "Please enter a name with fewer than 20 characters"
                          }
                        })
                      }
                       />
                       <div className="line"></div>
                       </div>
                       <span className="error-message">
                           {errors.name && errors.name.message}
                       </span>
                       {/* PHONE INPUT */}
                       <div className="text-center">
                       <input 
                       className="form-control"
                       type="text"
                       placeholder="Phone Number" 
                       name="phone"
                       />
                       <div className="line"></div>
                       </div>
                       {/* EMAIL INPUTS */}
                       <div className="text-center">
                       <input 
                       className="form-control"
                       type="text"
                       placeholder="Email" 
                       name="email"
                       />
                       <div className="line"></div>
                       </div>
                       {/* SUBJECT INPUT */}
                       <div className="text-center">
                       <input 
                       className="form-control"
                       type="text"
                       placeholder="Subject" 
                       name="subject"
                       />
                       <div className="line"></div>
                       </div>
                    </div>
                    <div className="col-md-6 col-xs-12">
                        {/* DESCRIPTION */}
                       <div className="text-center">
                       <textarea 
                       className="form-control"
                       type="text"
                       placeholder="Please describe shortly your project" 
                       name="description"
                       ></textarea>
                       <div className="line"></div>
                       </div>
                       <button className="btn-main-offer contact-btn" type="submit">Contact Me</button>
                    </div>
                </div>
                </form>
            </div>
        </div>
    )
}

export default Contacts
